    public Vendor_Main(Session session) {
        initComponents();
        this.Session = session;
        id = Session.getId();
        
        // Payment
        Invoices.tabulateData(invoices, InvoiceTable,id);
        Payment.tabulateData(payments, PaymentTable,id);
        //Payment.tabulateReceipt(payment, ReceiptTable,id);
        
        Receipt.tabulateData(receipts,ReceiptTable,id);
        
        vendors=new Vendor().Import();
        ArrayList<String> unitdata;
        unitdata=Functions.Read("Units.txt");
        String unitnumber="";
        for(Vendor r:vendors){
            String vendorId= r.getId();
            String name = r.getName();
            String email = r.getEmail();
            String password = r.getPassword();
            String role = r.getRole();
            String contactNo=r.getContactNo();
            String unitId = r.getUnitId();
            if(id.equals(vendorId)){
                
//
            for(String i:unitdata){
                String[] j = i.split(":");
                if(j[0].equals(unitId)){
                    unitnumber=j[1];
                    }
                }
                //set text for unitid??
                NameL.setText(name);
                UserIDL.setText(vendorId);
                EmailL.setText(email);
                PasswordL.setText(password);
                RoleL.setText(role);
                TelNoL.setText(contactNo);
                UnitIDL.setText(unitnumber);
                
                
                //set text for unitid??
                UserIDL2.setText(vendorId);
                RoleL2.setText(role);
                NameMOD.setText(name);
                EmailMOD.setText(email);
                PasswordMOD.setText(password);
                TelNoMOD.setText(contactNo);
                UnitIDL2.setText(unitnumber);
                
            }
        }
        dt(); //Input Date and Time
    }

